package loopstatement;

public class WhileloopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var=2;
		while(var<=5) {
			System.out.println(var+"selenium");
			var++;
		}
		System.out.println();
		System.out.println("********");
	
	 var=1;
		while(var>=0) {
		System.out.println(var+"webdriver");
		var++;
		
		}
	}
}



